$stmt = $conn->prepare("INSERT INTO nominations (student_id, name, email, programme, role, specialization, project, campus, avatar, categories) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)");

foreach ($campusStudents as $campus => $students) {
    foreach ($students as $student) {
        $categories = implode(',', $student['categories']);
        $stmt->bind_param(
            "ssssssssss",
            $student['id'],
            $student['name'],
            $student['email'],
            $student['programme'],
            $student['role'],
            $student['specialization'],
            $student['project'],
            $campus,
            $student['avatar'],
